import React,{useContext,useEffect,useState} from "react";
import axios from "axios";

const AppContext = React.createContext()


const allMealsUrl = 'https://www.themealdb.com/api/json/v1/1/search.php?s=aaaa'
const randomMealUrl = 'https://www.themealdb.com/api/json/v1/1/random.php'



const AppProvider = ({children})=>{
const [loading,setLoading] = useState(false)
   
  const [meals,setMeals] = useState([]) 
    
  //  . then async/await alsow invoke data here/function
    // .fetch (url ).then invoke it 
    // we are using useEffect here to fetch data or axios from any url 
   
    const fetchMeals = async (url) =>{
        setLoading(true)
        try{
            const {data} = await axios(url)
        setMeals(data.meals)
            
            
        }
        catch(error){
            console.log(error.response)
         }
         setLoading(false)
    }
    useEffect(()=>{
        // console.log('fetch data here')
        
        fetchMeals(allMealsUrl);
    },[])
    return <AppContext.Provider value={{loading,meals}}>
        {children}
    </AppContext.Provider>

}

export const useGlobalContext=()=>{
 return useContext(AppContext)   
}

export {AppContext,AppProvider};
