import React,{useContext,useEffect,useState} from "react";
import axios from "axios";

const AppContext = React.createContext()


const allMealsUrl = 'https://www.themealdb.com/api/json/v1/1/search.php?s='
const randomMealUrl = 'https://www.themealdb.com/api/json/v1/1/random.php'



const AppProvider = ({children})=>{
const [loading,setLoading] = useState(false)
const [meals,setMeals] = useState([]) 
const [searchTerm,setSearchTerm] = useState("")

const [showModal,setShowModal] = useState(false)
const [selectedMeal,setSelectedMeal] = useState(null)

  //  . then async/await alsow invoke data here/function
    // .fetch (url ).then invoke it 
    // we are using useEffect here to fetch data or axios from any url 
   
    const fetchMeals = async (url) =>{
        setLoading(true)
        try{
            const {data} = await axios(url)
        if(data.meals){
            setMeals(data.meals)
        }  
         else{
            setMeals([])
         }   
        }
        catch(error){
            console.log(error.response)
         }
         setLoading(false)
    }

    const fetchRandomMeal = ()=>{
        fetchMeals(randomMealUrl)
    }

 const selectMeal = (idMeal,favoriteMeal)=>{
 let meal;
 meal = meals.find((meal)=>meal.idMeal === idMeal)
 setSelectedMeal(meal)
 setShowModal(true);
 }


    useEffect(()=>{
        
        
        fetchMeals(allMealsUrl);
    },[])

    useEffect(()=>{
        // console.log('fetch data here')
        if(!searchTerm) return
        fetchMeals(`${allMealsUrl}${searchTerm}`);
    },[searchTerm])
    return <AppContext.Provider value={{loading,meals,
    setSearchTerm,fetchRandomMeal,showModal,selectedMeal,selectMeal }}>
        {children}
    </AppContext.Provider>
 
}

export const useGlobalContext=()=>{
 return useContext(AppContext)   
}

export {AppContext,AppProvider};
